import React, {useState} from "react";
import './CommentModal.css';




function CommentModal({post}){

    const [formData, setFormData] = useState({
        "name": '',
        'email': '',
        'content': '',
        "post": post,

    });


    function close(){
        let modal = document.getElementById("myModal");
        modal.style.display = "none";
    }

    function handleChange(e){
        setFormData({
            ...formData, [e.target.name]: e.target.value
        })
    }

    function handleSubmit(e){
        e.preventDefault();
        console.log (formData)
    }
    return(
        <>
       <div id="myModal" class="modal">

            <div class="modal-content">
            <span onClick={close} className="close">&times;</span>
            <form>
                <label for="name">Name:</label>
                    <input type="text" name="name" id="name" onChange={handleChange}></input>
                <label for="email">Email:</label>
                    <input type="email" name="email" id="email" onChange={handleChange}></input>
                <label for="content">Comment:</label>
                    <textarea name="content" id="content" onChange={handleChange}></textarea>
                <button onClick={handleSubmit}>Submit Comment</button>
            </form>
            </div>

        </div>

        </>
    );
}


export default CommentModal;
