import React, {useState, useEffect} from "react"
import axios from 'axios'
import './Home.css'

function Home(){

    const [title, setTitle] = useState("");
    const [content, setContent] = useState('');

    useEffect(
    async function getTitle(){
        let res = await axios.get('http://sweetolivefarms.com/wp-json/wp/v2/posts?per_page=1')
        let title = res.data[0].title.rendered
        console.log(title)
        setTitle(title)
    },[])

    return(
        <>
        <div className="home-main">
            <a href="/plants" className="home-inner plants">Plants</a>
            <a href="/recipes" className="home-inner recipes">Recipes</a>
            <a href="/projects" className="home-inner projects">Projects</a>
        </div>
        <div>
            <div className='home-title' dangerouslySetInnerHTML={{__html: title}}>

            </div>
        </div>
        </>
    );
}

export default Home;
