import React, {useState} from "react"
import Menu from './Menu'
import './MenuContainer.css'
import { faBars, faSearch} from '@fortawesome/free-solid-svg-icons'
import {faFacebook, faInstagram, faPinterest} from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

function MenuContainer(){

    const [visible, setVisible] = useState(true);

    function toggleVisible(){
        setVisible(!visible)
        document.getElementById('menu').className=`visible-${visible}`
    }
    return(
        <>
        <div className="MenuContainer-right">
        <span>
            <form>
                <input type="text" id="search" name="search" />
                <button onSubmit={handleSubmit}><FontAwesomeIcon icon={faSearch} /></button>    
            </form><FontAwesomeIcon icon={faFacebook} /> <FontAwesomeIcon icon={faInstagram} /> <FontAwesomeIcon icon={faPinterest} /></span>
        &nbsp;  &nbsp;  &nbsp;
        <span onClick={toggleVisible}><FontAwesomeIcon icon={faBars} /></span>
        </div>
        <Menu toggle={toggleVisible}/>
        <hr />
        </>
    );
}

export default MenuContainer;
