import React, {useContext, useState}from "react"
import './Posts.css'
import {useParams, Link, Redirect} from  'react-router-dom'
import AppContext from "./Context";
import {v4 as uuid} from 'uuid';
import {
    Card,
    CardBody,
    CardTitle,
    CardText
  } from "reactstrap";

function Posts(){
    let {handle} = useParams();
    let categoryIdMap= {
        "annuals": "314",
        "perennials" : "311"
    }

    let {postData, paramPosts} = useContext(AppContext)
    paramPosts(categoryIdMap[handle])

    let post = postData.current
    console.log(post)

    return(
        <>
         <div className="posts-left">
        <h2> {handle.charAt(0).toUpperCase() + handle.slice(1)}</h2>

      { post ? post.map(result => (
          <section key={uuid()}>
              <Card>
                <CardBody>
                    <Link to={`/post/${result.slug}`}>
                  <CardTitle className="font-weight-bold">
                  <div dangerouslySetInnerHTML={{__html: result.title.rendered}}></div>
                  </CardTitle>
                  </Link>
                  <CardText dangerouslySetInnerHTML={{__html: result.excerpt.rendered}}>
                  </CardText>
                </CardBody>
              </Card>
            </section>
    )): <h1> are you lost?</h1>}
        </div>
        </>
    );
}


export default Posts;
