import React, {useState} from "react"
import {Route, Switch, Redirect, useHistory} from 'react-router-dom'
import Logo from './Logo'
import Home from './routes/Home'
import Plants from './routes/Plants'
import Recipes from './routes/Recipes'
import MenuContainer from './MenuContainer'
import Posts from './Posts'
import Search from './routes/Search'
import './App.css';
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
import axios from "axios"
import AppContext from "./Context"
import Post from "./routes/Post"

library.add(fab)

function App() {
  const BASE_URL = 'http://sweetolivefarms.com/wp-json/wp/v2'

  const [searchData, setSearchData] = useState();
  const [postData, setPostData] = useState();

  let history = useHistory();


  async function search(formData){
    let searchTerm = formData.search;
    let res = await axios.get(`${BASE_URL}/posts?search=${searchTerm}`)
    let data = res.data;
    setSearchData(data);
    history.push('/search')
  };

  async function paramPosts(endpoint, handle, category){
    let posts = await axios.get(`${BASE_URL}/posts?categories=${category}`)
    let data = posts.data;
    setPostData(data)
    history.push(`/${endpoint}/${handle}`)

}

  return (
    <div className="App">
       <AppContext.Provider value={{postData, paramPosts }}>
      <MenuContainer search={search}/>
      <Logo />
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route exact path="/plants">
            <Plants />
          </Route>
          <Route exact path="/plants/:handle">
            <Posts />
          </Route>
          <Route exact path="/recipes">
            <Recipes />
          </Route>
          <Route path="/recipes/:handle">
            <Recipes />
          </Route>
          <Route path="/post/:handle">
            <Post />
          </Route>
          <Route path="/search">
            <Search searchData={searchData}/>
          </Route>
          <Redirect to="/" />
        </Switch>
        </AppContext.Provider>

    </div>
  );
}

export default App;
